mapper.select:
SELECT
		GUID	as "guid",  
		NAME	as "name",  
		MIN_CON	as "minCon",  
		MAX_CON	as "maxCon",  
		BALANCE	as "balance",  
		HEARBEAT	as "hearbeat",  
		CREATE_TIME	as "createTime",  
		CREATE_USER	as "createUser",  
		DBDRIVER	as "dbdriver",  
		DBTYPE	as "dbtype" 
FROM T_OC_DATAHOST


		and GUID = #{guid}


		and NAME = #{name}


		and MIN_CON = #{minCon}


		and MAX_CON = #{maxCon}


		and BALANCE = #{balance}


		and HEARBEAT = #{hearbeat}


		and CREATE_TIME = #{createTime}


		and CREATE_USER = #{createUser}


		and DBDRIVER = #{dbdriver}


		and DBTYPE = #{dbtype}


		and ${whereex}

SELECT COUNT(*) FROM T_OC_DATAHOST 


		and GUID = #{guid}


		and NAME = #{name}


		and MIN_CON = #{minCon}


		and MAX_CON = #{maxCon}


		and BALANCE = #{balance}


		and HEARBEAT = #{hearbeat}


		and CREATE_TIME = #{createTime}


		and CREATE_USER = #{createUser}


		and DBDRIVER = #{dbdriver}


		and DBTYPE = #{dbtype}
