mapper.select:
SELECT
		GUID	as "guid",  
		NAME	as "name",  
		CLASS	as "class",  
		MAP_FILE	as "mapFile",  
		CREATE_TIME	as "createTime",  
		CREATE_USER	as "createUser" 
FROM T_OC_FUNCTION


		and GUID = #{guid}


		and NAME = #{name}


		and CLASS = #{class}


		and MAP_FILE = #{mapFile}


		and CREATE_TIME = #{createTime}


		and CREATE_USER = #{createUser}


		and ${whereex}

SELECT COUNT(*) FROM T_OC_FUNCTION 


		and GUID = #{guid}


		and NAME = #{name}


		and CLASS = #{class}


		and MAP_FILE = #{mapFile}


		and CREATE_TIME = #{createTime}


		and CREATE_USER = #{createUser}
