mapper.select:
SELECT
		GUID	as "guid",  
		HOST	as "host",  
		URL	as "url",  
		D_USER	as "dUser",  
		PASSWORD	as "password",  
		time(CREATE_TIME)	as "createTime",  
		CREATE_USER	as "createUser",  
		PARENT_HOST	as "parentHost",  
		STATE	as "state" 
FROM T_OC_HOST


		and GUID = #{guid}


		and HOST = #{host}


		and URL = #{url}


		and D_USER = #{dUser}


		and PASSWORD = #{password}


		and CREATE_TIME = #{createTime}


		and CREATE_USER = #{createUser}


		and PARENT_HOST = #{parentHost}


		and STATE = #{state}


		and ${whereex}

SELECT COUNT(*) FROM T_OC_HOST 


		and GUID = #{guid}


		and HOST = #{host}


		and URL = #{url}


		and D_USER = #{dUser}


		and PASSWORD = #{password}


		and CREATE_TIME = #{createTime}


		and CREATE_USER = #{createUser}


		and PARENT_HOST = #{parentHost}


		and STATE = #{state}

SELECT GUID as "guid", HOST as "host", URL as "url", D_USER as "dUser", PASSWORD as "password", CREATE_TIME as "createTime", CREATE_USER as "createUser", PARENT_HOST as "parentHost", STATE as "state" FROM T_OC_HOST
and PARENT_HOST = '' OR PARENT_HOST IS null
and PARENT_HOST = #{id}

SELECT guid as "id", PARENT_HOST as "parentHost", HOST as "text", STATE as "state" FROM T_OC_HOST
and (PARENT_HOST = '' OR PARENT_HOST IS null)
and PARENT_HOST = #{id}
