mapper.select:
SELECT OT.GUID "guid", OT.NAME "name", OT.TYPE "type", OT.DATANODE "datanode", OT.RULE "rule", OT.PARENT_NAME "parentName", OT.JOINKEY "joinkey", OT.PARENTKEY "parentkey", OT.CREATE_TIME "createTime", OT.CREATE_USER "createUser" FROM T_OC_SCHEMA_MAP OS , T_OC_TABLE OT
OT.NAME = OS.TABLE_NAME
and GUID = #{guid}
and OS.SCHEMA_NAME = #{schemaName}
and TABLE_NAME = #{tableName}
and ${whereex}
order by OT.type desc

SELECT
GUID as "guid",  
SCHEMA_NAME as "schemaName",  
TABLE_NAME as "tableName" 
FROM T_OC_SCHEMA_MAP


		and GUID = #{guid}


		and SCHEMA_NAME = #{schemaName}


		and TABLE_NAME = #{tableName}


		and ${whereex}

SELECT COUNT(*) FROM T_OC_SCHEMA_MAP


		and GUID = #{guid}


		and SCHEMA_NAME = #{schemaName}


		and TABLE_NAME = #{tableName}

mapper.update:
UPDATE T_OC_SCHEMA_MAP


		GUID = #{guid, jdbcType=NVARCHAR},


		SCHEMA_NAME = #{schemaName, jdbcType=NVARCHAR},


		TABLE_NAME = #{tableName, jdbcType=NVARCHAR}



	GUID = #{guid}
