mapper.select:
SELECT
		GUID	as "guid",  
		NAME	as "name",  
		PASSWORD	as "password",  
		APP_NAME	as "appName",  
		APP_CODE	as "appCode",  
		CREATE_USER	as "createUser",  
		CREATE_TIME	as "createTime",  
		SCHEMAS	as "schemas" 
FROM T_OC_USER


		and GUID = #{guid}


		and NAME = #{name}


		and PASSWORD = #{password}


		and APP_NAME = #{appName}


		and APP_CODE = #{appCode}


		and CREATE_USER = #{createUser}


		and CREATE_TIME = #{createTime}


		and SCHEMAS = #{schemas}


		and ${whereex}

SELECT COUNT(*) FROM T_OC_USER 


		and GUID = #{guid}


		and NAME = #{name}


		and PASSWORD = #{password}


		and APP_NAME = #{appName}


		and APP_CODE = #{appCode}


		and CREATE_USER = #{createUser}


		and CREATE_TIME = #{createTime}


		and SCHEMAS = #{schemas}
