mapper.select:
SELECT
		guid	as "guid",  
		customer_code	as "customerCode",  
		customer_name	as "customerName",  
		product_company	as "productCompany",  
		product_code	as "productCode",  
		product_Name	as "productName",  
		expiring_date	as "expiringDate",  
		lic_version	as "licVersion",  
		license_code	as "licenseCode",  
		license_mode	as "licenseMode",  
		signing_date	as "signingDate",  
		start_date	as "startDate",  
		version_number	as "versionNumber",  
		signature	as "signature",  
		encrypt_model	as "encryptModel",  
		create_time	as "createTime" 
FROM t_sa_licenses


		and guid = #{guid}


		and customer_code = #{customerCode}


		and customer_name like '%${customerName}%'


		and product_company = #{productCompany}


		and product_code = #{productCode}


		and product_Name like '%${productName}%'


		and expiring_date = #{expiringDate}


		and lic_version = #{licVersion}


		and license_code = #{licenseCode}


		and license_mode = #{licenseMode}


		and signing_date = #{signingDate}


		and start_date = #{startDate}


		and version_number = #{versionNumber}


		and signature = #{signature}


		and encrypt_model = #{encryptModel}


		and create_time = #{createTime}

SELECT COUNT(*) FROM t_sa_licenses 


		and guid = #{guid}


		and customer_code = #{customerCode}


		and customer_name like '%${customerName}%'


		and product_company = #{productCompany}


		and product_code = #{productCode}


		and product_Name like '%${productName}%'


		and expiring_date = #{expiringDate}


		and lic_version = #{licVersion}


		and license_code = #{licenseCode}


		and license_mode = #{licenseMode}


		and signing_date = #{signingDate}


		and start_date = #{startDate}


		and version_number = #{versionNumber}


		and signature = #{signature}


		and encrypt_model = #{encryptModel}


		and create_time = #{createTime}
