mapper.select:
SELECT
		guid	as "guid",  
		code	as "code",  
		name	as "name",  
		public_key	as "publicKey",  
		private_key	as "privateKey",  
		modulus	as "modulus" 
FROM t_sa_rsa


		and guid = #{guid}


		and code = #{code}


		and name like '%${name}%'


		and public_key = #{publicKey}


		and private_key = #{privateKey}


		and modulus = #{modulus}

SELECT COUNT(*) FROM t_sa_rsa 


		and guid = #{guid}


		and code = #{code}


		and name like '%${name}%'


		and public_key = #{publicKey}


		and private_key = #{privateKey}


		and modulus = #{modulus}
