mapper.select:
SELECT
		BUTTON_GUID	as "buttonGuid",  
		BUTTON_NAME	as "buttonName",  
		BUTTON_CODE	as "buttonCode",  
		MODIFY_TIME	as "modifyTime",  
		ROLE_GUID	as "roleGuid",  
		ROLE_CODE	as "roleCode",  
		MODIFY_USER	as "modifyUser",  
		CREATE_TIME	as "createTime",  
		CREATE_USER	as "createUser",  
		REMARK	as "remark",  
		GUID	as "guid" 
FROM T_SYS_AUTHBUTTON


		and BUTTON_GUID = #{buttonGuid}


		and BUTTON_NAME = #{buttonName}


		and BUTTON_CODE = #{buttonCode}


		and MODIFY_TIME = #{modifyTime}


		and ROLE_GUID = #{roleGuid}


		and ROLE_CODE = #{roleCode}


		and MODIFY_USER = #{modifyUser}


		and CREATE_TIME = #{createTime}


		and CREATE_USER = #{createUser}


		and REMARK = #{remark}


		and GUID = #{guid}

SELECT COUNT(*) FROM T_SYS_AUTHBUTTON 


		and BUTTON_GUID = #{buttonGuid}


		and BUTTON_NAME = #{buttonName}


		and BUTTON_CODE = #{buttonCode}


		and MODIFY_TIME = #{modifyTime}


		and ROLE_GUID = #{roleGuid}


		and ROLE_CODE = #{roleCode}


		and MODIFY_USER = #{modifyUser}


		and CREATE_TIME = #{createTime}


		and CREATE_USER = #{createUser}


		and REMARK = #{remark}


		and GUID = #{guid}
