mapper.select:
SELECT
		GOMACODE	as "gomacode",  
		STATUS	as "status",  
		GOODSOWNERNAME	as "goodsownername",  
		DB_NAME	as "dbName",  
		CREATEDATE	as "createdate",  
		CREATENAME	as "createname",  
		GOODSOWNERID	as "goodsownerid" 
FROM T_SYS_OWNERMAPDB


		and GOMACODE = #{gomacode}


		and STATUS = #{status}


		and GOODSOWNERNAME like '%${goodsownername}%'


		and DB_NAME = #{dbName}


		and CREATEDATE = #{createdate}


		and CREATENAME = #{createname}


		and GOODSOWNERID = #{goodsownerid}

SELECT COUNT(*) FROM T_SYS_OWNERMAPDB 


		and GOMACODE = #{gomacode}


		and STATUS = #{status}


		and GOODSOWNERNAME like '%${goodsownername}%'


		and DB_NAME = #{dbName}


		and CREATEDATE = #{createdate}


		and CREATENAME = #{createname}


		and GOODSOWNERID = #{goodsownerid}
