mapper.select:
SELECT
		guid	as "guid",  
		role_type_code	as "roleTypeCode",  
		role_type_name	as "roleTypeName",  
		role_type_group	as "roleTypeGroup",  
		status	as "status",  
		remark	as "remark",  
		sort_index	as "sortIndex",  
		create_time	as "createTime",  
		create_user	as "createUser" 
FROM t_sys_roletype


		and guid = #{guid}


		and role_type_code = #{roleTypeCode}


		and role_type_name like '%${roleTypeName}%'


		and status = #{status}


		and remark = #{remark}


		and sort_index = #{sortIndex}


		and create_time = #{createTime}


		and create_user = #{createUser}


order by role_type_group,sort_index

SELECT COUNT(*) FROM t_sys_roletype 


		and guid = #{guid}


		and role_type_code = #{roleTypeCode}


		and role_type_name like '%${roleTypeName}%'


		and status = #{status}


		and remark = #{remark}


		and sort_index = #{sortIndex}


		and create_time = #{createTime}


		and create_user = #{createUser}
