mapper.select:
SELECT
		GUID	as "guid",  
		FUN_NAME	as "funName",  
		FUN_CODE	as "funCode",  
		FUN_DESC	as "funDesc",  
		IS_DISPLAY  as "isDisplay",
		REMARK	as "remark",  
		CREATE_USER	as "createUser",  
		CREATE_TIME	as "createTime" 
FROM T_SYS_SETUP


		and GUID = #{guid}


		and FUN_NAME = #{funName}


		and FUN_CODE = #{funCode}


		and FUN_DESC = #{funDesc}


		and IS_DISPLAY = #{isDisplay}


		and REMARK = #{remark}


		and CREATE_USER = #{createUser}


		and CREATE_TIME = #{createTime}


		and ${whereex}

SELECT COUNT(*) FROM T_SYS_SETUP 


		and GUID = #{guid}


		and FUN_NAME = #{funName}


		and FUN_CODE = #{funCode}


		and FUN_DESC = #{funDesc}


		and IS_DISPLAY = #{isDisplay}


		and REMARK = #{remark}


		and CREATE_USER = #{createUser}


		and CREATE_TIME = #{createTime}


		and ${whereex}
