mapper.select:
SELECT A.GUID as "guid", A.LOGIN_ID as "loginId", A.NAME as "name", A.PASSWORD as "password", A.ORG_CODE as "orgCode" FROM T_SYS_USER A,T_SYS_AUTHUSER B
A.LOGIN_ID = B.USER_CODE AND B.ROLE_CODE = #{roleCode}
AND LOGIN_ID = #{loginId}
AND NAME LIKE '%${name}%'
AND ORG_CODE = #{orgCode}

SELECT COUNT(*)
FROM T_SYS_USER A,T_SYS_AUTHUSER B  

	A.LOGIN_ID = B.USER_CODE AND B.ROLE_CODE = #{roleCode}

		AND LOGIN_ID = #{loginId}


		AND NAME LIKE '%${name}%'


		AND ORG_CODE = #{orgCode}

SELECT
		guid	as "guid",  
		login_id	as "loginId",  
		name	as "name",  
		password	as "password",  
		alive_flag	as "aliveFlag",  
		create_time	as "createTime",  
		create_user	as "createUser",  
		mobile_phone	as "mobilePhone",  
		address	as "address",  
		age	as "age",  
		sex	as "sex",  
		sort_index	as "sortIndex",  
		org_code	as "orgCode",  
		in_join_time	as "inJoinTime",  
		work_status	as "workStatus",  
		e_mail	as "eMail" ,
		is_online	as "isOnline" ,
		is_verify	as "isVerify" 
FROM t_sys_user


		and guid = #{guid}


		and login_id = #{loginId}


		and name like '%${name}%'


		and password = #{password}


		and alive_flag = #{aliveFlag}


		and create_time = #{createTime}


		and create_user = #{createUser}


		and mobile_phone = #{mobilePhone}


		and address = #{address}


		and age = #{age}


		and sex = #{sex}


		and sort_index = #{sortIndex}


		and org_code = #{orgCode}


		and in_join_time = #{inJoinTime}


		and work_status = #{workStatus}


		and e_mail = #{eMail}


		and is_verify = #{isVerify}


		and is_online = #{isOnline}

SELECT COUNT(*) FROM t_sys_user 


		and guid = #{guid}


		and login_id = #{loginId}


		and name like '%${name}%'


		and password = #{password}


		and alive_flag = #{aliveFlag}


		and create_time = #{createTime}


		and create_user = #{createUser}


		and mobile_phone = #{mobilePhone}


		and address = #{address}


		and age = #{age}


		and sex = #{sex}


		and sort_index = #{sortIndex}


		and org_code = #{orgCode}


		and in_join_time = #{inJoinTime}


		and work_status = #{workStatus}


		and e_mail = #{eMail}


		and is_verify = #{isVerify}


		and is_online = #{isOnline}
