mapper.select:
SELECT
		GUID	as "guid",  
		WSNAME	as "wsname",  
		INTFGUID	as "intfguid",  
		WSTYPE	as "wstype",  
		SENDSOURCE	as "sendsource",  
		PRAMDATA	as "pramdata",  
		RECODE	as "recode",  
		RECONTENT	as "recontent",  
		SENDTIME	as "sendtime",  
		RETIME	as "retime" 
FROM T_SYS_WSLOG


		and GUID = #{guid}


		and WSNAME = #{wsname}


		and INTFGUID = #{intfguid}


		and WSTYPE = #{wstype}


		and SENDSOURCE = #{sendsource}


		and PRAMDATA = #{pramdata}


		and RECODE = #{recode}


		and RECONTENT = #{recontent}


		and SENDTIME = #{sendtime}


		and RETIME = #{retime}


		and ${whereex}

SELECT COUNT(*) FROM T_SYS_WSLOG 


		and GUID = #{guid}


		and WSNAME = #{wsname}


		and INTFGUID = #{intfguid}


		and WSTYPE = #{wstype}


		and SENDSOURCE = #{sendsource}


		and PRAMDATA = #{pramdata}


		and RECODE = #{recode}


		and RECONTENT = #{recontent}


		and SENDTIME = #{sendtime}


		and RETIME = #{retime}
