mapper.select:
SELECT
		GUID	as "guid",  
		snmpname as "name",  
		IP	as "ip",  
		PORT	as "port",  
		community	as "community",  
		FILE_NAME as "fileName"
FROM T_SYS_MYCAT_SNMP


		and GUID = #{guid}


		and SNMPNAME = #{name}


		and IP = #{ip}


		and PORT = #{port}


		and community = #{community}
