mapper.select:
select START_TIME,START_TM,EXECUTE_TIME,ID,USER,DB_NAME,CONTENT as "SQL",IPAddr from t_mycat_syssql 
 
	
		and DB_NAME = #{DB_NAME}
	 		
	
		and ID = #{ID}
	 
	
		and START_TIME = #{START_TIME}
		
	 
		and USER =  #{username}
							

 limit 1;

select SID,START_TIME,START_TM,EXECUTE_TIME,ID,USER,DB_NAME,CONTENT as "SQL",IPAddr from t_mycat_syssql 
 
	
		and DB_NAME = #{DB_NAME}
	 			
	
		and ID = #{ID}
	
	
		and START_TM between #{begin} and #{end}
	
	 
		and USER =  #{username}
				 

order by START_TM desc

select count(1) from t_mycat_syssql 
 
	
		and DB_NAME = #{DB_NAME}
	 	
	
		and ID = #{ID}
	
	
		and START_TM between #{begin} and #{end}
	
	 
		and USER =  #{username}
